package ssafy;

import java.util.Scanner;

public class Main537 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner scan = new Scanner(System.in);
		int in = scan.nextInt();
		int sum = 0;
		for (int i = 1; i <= in ; i++) {
			sum += i;
		}
		System.out.println(sum);

	}

}
